lm_intercept <- calc(pptime, fun = function(x) {
  if (all(is.na(x)))
    return(NA)
  else
    return(coef(lm(x ~ time))[1])
})

lm_slope <- calc(pptime, fun = function(x) {
  if (all(is.na(x)))
    return(NA)
  else
    return(coef(lm(x ~ time))[2])
})

sss=lm_slope * 50 + lm_intercept













